INSERT INTO `book_list` VALUES ('354', '玄幻小说', '原始部落大冒险', '作    者：马一角', '    一个人穿越到了史前原始部落，他该怎么办？    风云的答案是：活着，好好地活着，利用自己的知识活得更好。可能的话，出去走一走，看一看，探一探险。\n', 'http://www.xbiquge.la/7/7989/', 'http://www.xbiquge.la/files/article/image/7/7989/7989s.jpg', '最后更新：2019-12-21 23:25:33', ' 第两千七百零五章 功效惊人', 'http://www.xbiquge.la/7/7989/21318557.html');
