INSERT INTO `book_list` VALUES ('1905', '都市小说', '都市奇门医圣', '作    者：一念', '    实习医生叶皓轩，意外的得到一本古书上的玄术与医道传承，自此开始了不一样的人生，他银针渡人，术法渡鬼，成就济世仁心，都市生活逍遥自在，校花、御姐加熟女，教师、医生和白领！且看叶皓轩如何在这风骚的大都市混迹花丛。\n\n', 'http://www.xbiquge.la/4/4627/', 'http://www.xbiquge.la/files/article/image/4/4627/4627s.jpg', '最后更新：2019-12-21 19:22:11', '第4099章 发呆', 'http://www.xbiquge.la/4/4627/21315102.html');
