INSERT INTO `book_list` VALUES ('1982', '都市小说', '老婆爱上我', '作    者：傲无常', '    泡妞的最高境界是泡老婆！    特种兵王刘青和新婚妻子，从陌生如何一步一步走向彼此信任，其中的精彩纷呈任君品尝！', 'http://www.xbiquge.la/34/34570/', 'http://www.xbiquge.la/files/article/image/34/34570/34570s.jpg', '最后更新：2019-03-13 18:26:36', '老傲都市类新作： 花都十二钗 求支持', 'http://www.xbiquge.la/34/34570/16053967.html');
