INSERT INTO `book_list` VALUES ('1984', '都市小说', '全职国医', '作    者：方千金', '    　　中医学院的应届毕业生方寒，原本只想当一名安安静静的帅医生，取个漂亮的媳妇，退休之前混个主任医师职称，却没想到一不小心成了最年轻的大医国手。', 'http://www.xbiquge.la/36/36328/', 'http://www.xbiquge.la/files/article/image/36/36328/36328s.jpg', '最后更新：2019-12-22 02:13:15', '第五百九十三章 接二连三的大拿', 'http://www.xbiquge.la/36/36328/21321519.html');
INSERT INTO `book_list` VALUES ('1985', '都市小说', '荒原闲农', '作    者：醛石', '    魔都混不下去的苍海在一番奇遇之后，回到西部荒芜的老家，面对满目的黄土，延绵的丘陵，打井种地，把原本只见黄土不见绿的荒原一步步打造成世外桃源。    一条黄狗，一头毛驴，种几倾瓜，门前几块菜地，屋旁山下再来几亩乱种的果树，四季有果，岁岁有花，每日清茶农家饭，下棋看书，悠然自得。', 'http://www.xbiquge.la/28/28568/', 'http://www.xbiquge.la/files/article/image/28/28568/28568s.jpg', '最后更新：2019-09-02 19:38:29', '第 600章 终章', 'http://www.xbiquge.la/28/28568/19473332.html');
INSERT INTO `book_list` VALUES ('1986', '都市小说', '夫人，你马甲又掉了！', '作    者：一路烦花', '    【腹黑慵懒巨有钱男主vs高岭之花藏得深女主】    秦苒，从小在乡下长大，高三失踪一年，休学一年。    一年后，她被亲生母亲接到云城一中借读。    母亲说：你后爸是名门之后，你大哥自小就是天才，你妹妹是一中尖子生，你不要丢他们的脸。    **    京城有身份的人暗地里都收到程家隽爷的一份警告：隽爷老婆是乡下人，不懂圈子不懂时势不懂金融行情……脾气还差的很，总之，大家多担待。    直到一天，隽爷调查某个大佬时，他的手下望着不小心扒出来的据说什么都不懂的小嫂子的其中一个马甲……陷入迷之沉默。    大概就是两个大佬为了不让对方自卑，互相隐藏马甲的故事。', 'http://www.xbiquge.la/39/39328/', 'http://www.xbiquge.la/files/article/image/39/39328/39328s.jpg', '最后更新：2019-12-22 04:08:32', '649晨大佬卷14： 你何晨有什么不敢的？', 'http://www.xbiquge.la/39/39328/21321743.html');
INSERT INTO `book_list` VALUES ('1987', '都市小说', '我爸真是大明星', '作    者：肉肉嗒', '    胡烁坚信不会写书的演员不是好奶爸！！！    于是，他走上了主职带娃兼职混文娱圈的悠闲生活……    哄女儿睡觉的故事全集随便就能卖个千万册；    教女儿唱首儿歌一不小心就成了街知巷闻的神曲；    拍部动画片给女儿看吧，却创下了国产动漫的票房记录；    面对记者的镜头，胡烁无奈的摊了摊手：其实，我只是想带好娃儿而已……    ps：本书又名《千亿奶爸的文娱生活》，没错，主角有个身价千亿的老婆……', 'http://www.xbiquge.la/43/43544/', 'http://www.xbiquge.la/files/article/image/43/43544/43544s.jpg', '最后更新：2019-12-22 09:19:41', ' 447，现场写歌（5/5）', 'http://www.xbiquge.la/43/43544/21323111.html');
INSERT INTO `book_list` VALUES ('1988', '都市小说', '神级投资', '作    者：拼命七郎666', '    当幸运女神站在你的背后，你想要的财富、地位、女人都会纷至杳来！    2019，我为金钱代言！', 'http://www.xbiquge.la/30/30753/', 'http://www.xbiquge.la/files/article/image/30/30753/30753s.jpg', '最后更新：2019-11-01 13:04:47', '新书《最强战神》发布公告！', 'http://www.xbiquge.la/30/30753/21313271.html');
INSERT INTO `book_list` VALUES ('1989', '都市小说', '终极学生在都市', '作    者：日暮客愁', '    都说，人只有在绝境中才能更好的认识上帝，上帝为你关上一扇门，会为你打开一扇窗……李泽道不相信这句话，但是，上帝的确这么干了！\n    ...\n', 'http://www.xbiquge.la/14/14543/', 'http://www.xbiquge.la/files/article/image/14/14543/14543s.jpg', '最后更新：2019-12-22 02:05:00', '第二千三百六十六章 强盗', 'http://www.xbiquge.la/14/14543/21321509.html');
INSERT INTO `book_list` VALUES ('1990', '都市小说', '女帝家的小白脸', '作    者：袖里箭', '    户部大臣抱着厚厚的《经济学》每天研读。\n    刑部上下每天都在钻研《宪法》。\n    工部所有人看着主角弄出来的蒸汽机无从下手。\n    兵部的人拿着合金钢刀耀武扬威。\n    这是一个地球人在异界培养科技党的故事。\n    主角拿着火药，看着一群人在那欢快的徒手拆城墙，心中一万头草泥马奔腾而过。\n', 'http://www.xbiquge.la/14/14816/', 'http://www.xbiquge.la/files/article/image/14/14816/14816s.jpg', '最后更新：2019-12-21 03:08:03', '第一千二百一十四章 抵达八臂族', 'http://www.xbiquge.la/14/14816/21308269.html');
INSERT INTO `book_list` VALUES ('1991', '都市小说', '华娱之闪耀巨星', '作    者：万乘北宸', '    重回2001，聂唯要做娱乐圈最闪耀的那颗星。\n', 'http://www.xbiquge.la/8/8286/', 'http://www.xbiquge.la/files/article/image/8/8286/8286s.jpg', '最后更新：2019-12-21 23:42:09', '1524.《华夏机长》杀青（下）', 'http://www.xbiquge.la/8/8286/21318889.html');
INSERT INTO `book_list` VALUES ('1992', '都市小说', '豪门龙婿', '作    者：梅田丸子', '    曾经年幼家贫，被人欺，被人叫哈巴狗，我无能为力。一夜之间，我变成了六年后的自己，成了女校花的上门女婿，我立誓：从今以后，我不是哈巴狗，老子要做枭雄！    主角一夜长大六岁，怎么办？', 'http://www.xbiquge.la/36/36086/', 'http://www.xbiquge.la/files/article/image/36/36086/36086s.jpg', '最后更新：2019-04-17 17:12:12', '第68章 终成眷属（全书完）', 'http://www.xbiquge.la/36/36086/16997064.html');
INSERT INTO `book_list` VALUES ('1993', '都市小说', '最佳影星', '作    者：白色十三号', '    我要躺在钞票堆里打滚！我要跟一群美女开趴！我要做好莱坞巨星！\n', 'http://www.xbiquge.la/15/15105/', 'http://www.xbiquge.la/files/article/image/15/15105/15105s.jpg', '最后更新：2018-08-31 10:54:18', '新书《魔气熏天》（这章不要订阅）', 'http://www.xbiquge.la/15/15105/10368503.html');
INSERT INTO `book_list` VALUES ('1994', '都市小说', '疯狂农民工', '作    者：弹剑吟诗啸', '    夏建,被人戏称下贱。先是和村里的小寡妇闹绯闻,后因高中女同学和社会青年打群架而被学校开除,从此他流的是汗,燃烧的是情。\n    磨练算得了什么,阴谋,权势,黑道,统统玩转。\n    请看他一个小小村官是如何脚扫险恶,手控官场,一路红颜的。\n    本书男女老少皆宜,戳进来吧！绝对不一样的精彩！\n', 'http://www.xbiquge.la/14/14185/', 'http://www.xbiquge.la/files/article/image/14/14185/14185s.jpg', '最后更新：2019-12-20 09:41:41', '第2461章 看望故人', 'http://www.xbiquge.la/14/14185/21298888.html');
INSERT INTO `book_list` VALUES ('1995', '都市小说', '医品宗师', '作    者：步行天下', '', 'http://www.xbiquge.la/14/14718/', 'http://www.xbiquge.la/files/article/image/14/14718/14718s.jpg', '最后更新：2019-12-21 23:10:59', '第三百五十二章 成功破解程序！', 'http://www.xbiquge.la/14/14718/21318313.html');
INSERT INTO `book_list` VALUES ('1996', '都市小说', '我只想享受人生', '作    者：外汇似海', '    碌碌无为的小人物重回大一，改写命运，做投资大鳄，成财富赢家，收拾绿茶前任们，享受被爱，享受青春，享受生活，享受那多姿多彩的人生。    在场记者问：“尊敬的林先生，您的小目标也是赚一个亿吗？”他觉得自己低调太久了，零花钱都不止一个亿，居然成了小目标？    于是他淡淡道：“额，我的小目标早已实现了，是赚300亿美元！”全场观众们鸦雀无声，主持人呆若木鸡，众大佬们满脸不可思议，记者们话筒都掉了一地！    QQ群572733113', 'http://www.xbiquge.la/36/36266/', 'http://www.xbiquge.la/files/article/image/36/36266/36266s.jpg', '最后更新：2019-12-22 12:00:00', '713古董拍卖会', 'http://www.xbiquge.la/36/36266/21324315.html');
INSERT INTO `book_list` VALUES ('1997', '都市小说', '神级农场', '作    者：钢枪里的温柔', '    退役特战精英夏若飞，意外发现祖传画卷中竟内含乾坤，自带一方独立的小空间。    空间里有促进植物生长的灵潭水，有医治百病的神秘灵花，还自带十倍时间流速，栽什么都是精品，种什么都长得飞快，养什么都能通灵性……    从此，栽栽花、种种树、养养动物、撩撩美女，夏若飞开启了一段精彩纷呈的别样人生。    ------------------------------    已完成130万字作品《重生之长风破浪》、460万字作品《超极品纨绔》，人品保证，请放心收藏阅读，书荒的亲们可移步老书开宰。    ------------------------------    【书友群】300769830\n', 'http://www.xbiquge.la/10/10524/', 'http://www.xbiquge.la/files/article/image/10/10524/10524s.jpg', '最后更新：2019-12-21 23:59:51', '第一千五百零九章 小别重逢', 'http://www.xbiquge.la/10/10524/21319556.html');
INSERT INTO `book_list` VALUES ('1998', '都市小说', '符法逆天', '作    者：没落的游吟诗人', '    混沌符道，万法归一，符法无边，战无不胜！\n', 'http://www.xbiquge.la/0/147/', 'http://www.xbiquge.la/files/article/image/0/147/147s.jpg', '最后更新：2015-06-22 19:40:38', '本书已经圆满完本，一百八十万字了，已经足', 'http://www.xbiquge.la/0/147/2110592.html');
INSERT INTO `book_list` VALUES ('1999', '都市小说', '完美人生', '作    者：刀一耕', '    李谦重生了。\n    另外一个时空的1995年。\n    在这里，他当然比普通人更容易获得成功。\n    但成功是什么？\n    钱么？或者，名气？地位？荣耀？\n    都是，但不全是。\n    有了那回眸的浅浅一笑，那牵手的刹那温暖，那入怀的淡淡体香；\n    这人生，才称得上完美。\n', 'http://www.xbiquge.la/4/4532/', 'http://www.xbiquge.la/files/article/image/4/4532/4532s.jpg', '最后更新：2019-08-17 19:09:00', '新书《匹夫仗剑大河东去》三万字啦！', 'http://www.xbiquge.la/4/4532/19111307.html');
INSERT INTO `book_list` VALUES ('2000', '都市小说', '画满田园', '作    者：养只猫挠你', '    玄妙儿从当代画家，穿越到了家徒四壁的古代农家，\n    不过没关系，自己只要一笔在手，不怕赚不来银子。\n    执笔：\n    画一幅万里江山，\n    画一幅锦绣田园。\n    画一幅金玉满堂，\n    画一幅锦瑟和鸣。\n', 'http://www.xbiquge.la/8/8693/', 'http://www.xbiquge.la/files/article/image/8/8693/8693s.jpg', '最后更新：2019-12-22 08:15:00', '第四千零四十四章 等待着时机', 'http://www.xbiquge.la/8/8693/21322599.html');
INSERT INTO `book_list` VALUES ('2001', '都市小说', '绝色总裁的极品狂兵', '作    者：青衫金顶', '    他是战场上的王者，也是最强的教官！他教出了无数兵王，只等他一声号令！    一代兵王教头回归都市，开启了一段妖孽般的传奇人生！', 'http://www.xbiquge.la/21/21960/', 'http://www.xbiquge.la/files/article/image/21/21960/21960s.jpg', '最后更新：2019-12-22 00:05:00', '第2278章 中枢', 'http://www.xbiquge.la/21/21960/21320216.html');
INSERT INTO `book_list` VALUES ('2002', '都市小说', '绝品强少', '作    者：步履无声', '    我有三个爷爷。大爷爷是华夏神医。二爷爷是古武高手。三爷爷是杀手之王。告诉我，我怎么能低调？金刚怒目，所以降伏四魔，菩萨低眉，所以慈悲六道。与阎罗夺命，与天下争锋，要做，就做到医手遮天！\n', 'http://www.xbiquge.la/4/4539/', 'http://www.xbiquge.la/files/article/image/4/4539/4539s.jpg', '最后更新：2018-09-22 08:27:36', '完结感言', 'http://www.xbiquge.la/4/4539/10939045.html');
INSERT INTO `book_list` VALUES ('2003', '都市小说', '神级奖励系统', '作    者：仓库管理盐', '    哈？我被系统附身？    啥？世界被系统修改了？    纳尼！电影、游戏、音乐、小说……我所知道的都没有了？    这个世界太疯狂了，不过…    “嘿嘿，我喜欢！”    这是一个十分欢乐逗比，又有些腹黑的故事.    书友群： 105405002    作者自定义标签:                                                腹黑                                                系统流                                                宅男', 'http://www.xbiquge.la/26/26221/', 'http://www.xbiquge.la/files/article/image/26/26221/26221s.jpg', '最后更新：2019-07-22 11:41:00', '第1217章 返回', 'http://www.xbiquge.la/26/26221/18717543.html');
INSERT INTO `book_list` VALUES ('2004', '都市小说', '都市之国术无双', '作    者：鱼儿小小', '    “太极、八卦、形意、八极、咏春、谭腿……”苏辰学贯中西，学习对手，击败对手。他自豪的说：“芯片在手，天下我有。”\n    人的身体中隐藏着大秘密。苏辰为了生活，从拳手陪练起步，练国术，破强敌，一点点激发生命潜力。\n    无意中发现这世界的真正面目，地球所有人类都为了进化而努力，苏辰用一张学习芯片揭露天地奥秘，与世界各国精英争夺进化名额，超越众生。\n    ＰＳ：求收藏，推荐，订阅，打赏。品质保证，不断更，必完本。\n', 'http://www.xbiquge.la/7/7519/', 'http://www.xbiquge.la/files/article/image/7/7519/7519s.jpg', '最后更新：2018-11-09 17:35:49', '新书全民武道上架疯狂爆发，请老书友前去观看。', 'http://www.xbiquge.la/7/7519/12549822.html');
INSERT INTO `book_list` VALUES ('2005', '都市小说', '全球制造', '作    者：二将', '    计算机系的陈序，在帮人修电脑的时候，无意间创造出一个智能生命，世界因此而改变。    写网文，一秒钟百万字。机器翻译，全球从此无障碍交流。自动驾驶，世界上再也没有女司机这个物种。    人造视网膜，让你从    “四眼仔”变成    “靓仔”。生命工程科学，解开基因封锁，长生不死不再是神话传说……', 'http://www.xbiquge.la/39/39866/', 'http://www.xbiquge.la/files/article/image/39/39866/39866s.jpg', '最后更新：2019-11-26 23:44:46', '第295章 终章加感言', 'http://www.xbiquge.la/39/39866/20854764.html');
INSERT INTO `book_list` VALUES ('2006', '都市小说', '跑男之纯情巨星', '作    者：低空飞行', '    一场意外的爆炸，让小小的跑龙套从此有了不同的人生。面对女神们青眼有加，他总是显得无所适从。欢快的都市生活，从此不再寂寞，女神、金钱，还有那不变的信念……\n', 'http://www.xbiquge.la/10/10706/', 'http://www.xbiquge.la/files/article/image/10/10706/10706s.jpg', '最后更新：2018-08-14 08:53:52', '正文 第三千六百四十七章  终章：觉醒37', 'http://www.xbiquge.la/10/10706/10006992.html');
INSERT INTO `book_list` VALUES ('2007', '都市小说', '极品公子', '作    者：烽火戏诸侯', '    叶无道，一个以卫道者自居的花花公子，以征服美女为最大乐趣！为什么要把女人当作神圣不可侵犯的女神？为什么我不可以把她们当作我的玩物，当作我利用的棋子，当作我的奴隶？\n    钱，他将继承一个过百亿美元资产庞大家族的财产，多得可以当用美金当纸烧；\n    貌，他妈妈是省花，爸爸是风流倜傥，长得堪称女人的“杀手”；\n    既然上帝如此慷慨赋予身为两个庞大家族第三代中唯一的男性公民的他这么多，他还有什么理由不去征服美女——上帝送给男人的最好礼物？\n', 'http://www.xbiquge.la/11/11628/', 'http://www.xbiquge.la/modules/article/images/nocover.jpg', '最后更新：2017-11-09 06:59:54', '极品第一部结束，第二部《枭雄》酝酿中', 'http://www.xbiquge.la/11/11628/7839159.html');
INSERT INTO `book_list` VALUES ('2008', '都市小说', '重生浪潮之巅', '作    者：佛即心兮', '    当这个时代到来的时候，锐不可当，万物肆意生长，尘埃与曙光升腾，江河汇聚成川，无名山丘崛起为峰，天地一时，无比开阔。    于是重回1990年的方辰，一头扎进了时代的浪潮中。这一年岁月正好。    群号：705270966', 'http://www.xbiquge.la/33/33173/', 'http://www.xbiquge.la/files/article/image/33/33173/33173s.jpg', '最后更新：2019-12-21 23:57:30', '第八六二章 我见过方总您', 'http://www.xbiquge.la/33/33173/21319383.html');
INSERT INTO `book_list` VALUES ('2009', '都市小说', '开个诊所来修仙', '作    者：李闲鱼', '    我是天生的善恶中间人，天外诊所的主人，我治百病也替天行道。我的故事从一条狗开始，那一夜它送了我一个诊所……', 'http://www.xbiquge.la/6/6018/', 'http://www.xbiquge.la/files/article/image/6/6018/6018s.jpg', '最后更新：2019-12-22 11:02:38', '1734章 教材级撩骚', 'http://www.xbiquge.la/6/6018/21323814.html');
INSERT INTO `book_list` VALUES ('2010', '都市小说', '龙傲战神', '作    者：零零九', '    少年龙傲，十年沉寂，神秘龙印，炼魂血池，踏着累累尸骨一步步踏上武道巅峰！\n    问苍茫大地，何人敢与我一战？\n    以荒芜圣体，修炼无敌功法，炼化天地万物，吞噬无尽星辰，横扫荒宇，睥睨苍穹！\n    你命由我不由天，灭你只在挥手间！\n    书友群：217135300\n', 'http://www.xbiquge.la/2/2823/', 'http://www.xbiquge.la/files/article/image/2/2823/2823s.jpg', '最后更新：2017-11-09 06:45:50', '第三千四百六十章 主宰【大结局】', 'http://www.xbiquge.la/2/2823/7209840.html');
INSERT INTO `book_list` VALUES ('2011', '都市小说', '特种兵痞在都市', '作    者：一抹沉香', '    体内被注入病毒，为了活命必须靠近女人？痛失修为的特种兵痞重返都市，身在盛产美女的    “剩女之都”，万花丛中过，怎能不湿身？红颜祸水，人生艰险，恩怨情仇……且看他重返巅峰，王者归来，一指划过，如何演绎绚烂江湖。', 'http://www.xbiquge.la/24/24895/', 'http://www.xbiquge.la/files/article/image/24/24895/24895s.jpg', '最后更新：2019-12-11 20:56:29', '第4254章法族天骄！', 'http://www.xbiquge.la/24/24895/21156973.html');
INSERT INTO `book_list` VALUES ('2012', '都市小说', '重生之都市修仙', '作    者：十里剑神', '    正式版：渡劫期大修士陈凡陨落在天劫中，却一梦五百年重回地球的年少时代。\n    上一世我登临宇宙之巅，俯瞰万界，却无人相伴。这一世只愿不负前尘不负卿。\n    通俗版：修行五百年的渡劫期修仙者重生回都市，弥补遗憾，扮猪吃老虎的故事。\n', 'http://www.xbiquge.la/11/11496/', 'http://www.xbiquge.la/files/article/image/11/11496/11496s.jpg', '最后更新：2019-01-11 11:18:15', '需要修改下，等一段时间就会放出来的^_^', 'http://www.xbiquge.la/11/11496/14447558.html');
INSERT INTO `book_list` VALUES ('2013', '都市小说', '重生之绝世废少', '作    者：弼老耶', '    　　叶天，修仙界绝世天骄，修罗战神，却因功高震主，惨死于庆功楼，魂穿来到地球，重生在一个废物大少身上。    　　带着前世的修仙记忆，叶天强势崛起，血战强敌，逆天而行，纵横都市！    　　他来自修仙界，终将破碎虚空而去！', 'http://www.xbiquge.la/26/26108/', 'http://www.xbiquge.la/files/article/image/26/26108/26108s.jpg', '最后更新：2019-12-22 10:04:33', '第九百一十三章 采采补补', 'http://www.xbiquge.la/26/26108/21323453.html');
INSERT INTO `book_list` VALUES ('2014', '都市小说', '重生之修罗归来', '作    者：琅琊一号', '    　　三年前，青州裴家有子裴君临，风华绝代，三岁初蒙，七岁明劲，十二岁暗劲大成，十八岁时武破宗师。    　　然，裴君临突然暴毙，一身骨骼乃至精血全都被人挖走，只剩一张人皮，引发整个武道界大地震。    　　三年后，一名赘婿，逆天崛起！　　我为修罗，脚下当白骨累累，尸山血海！    　　重生归来，敢叫日月换新天，君临天下！    　　正版读者群：676088881', 'http://www.xbiquge.la/26/26132/', 'http://www.xbiquge.la/files/article/image/26/26132/26132s.jpg', '最后更新：2019-12-22 00:05:01', '七八二章 专一的好男人！', 'http://www.xbiquge.la/26/26132/21319952.html');
INSERT INTO `book_list` VALUES ('2015', '都市小说', '重生之最好时代', '作    者：九灯和善', '    上小学的时候，隔壁邻居的孩子考上了大学，你听到父母说清华和北大是国内最好的大学，小小的你心里在纠结，到底是去清华好还是北大好。    后来再大点，你知道了什么是文科什么是理科，你开始决定理科就考清华，文科就考北大。    最后你觉得清华太大容易迷路，下定决心选择了北大青鸟。    16岁那年，那个时候你觉得自己将来的座驾不是法拉利也是保时捷。    21岁的那年，你把奔驰和宝马定为了自己的目标。    25岁的时候，感觉十几万的合资车应该更加适合自己。    到了30岁的时候，对档次已经是没有了感觉，只要能有一辆属于自己的车就很满足了。    33岁那年，你觉得公交车上有空位，便已经是觉得很欣慰了。', 'http://www.xbiquge.la/40/40411/', 'http://www.xbiquge.la/files/article/image/40/40411/40411s.jpg', '最后更新：2019-12-21 15:23:18', '第350章 惊天大反转', 'http://www.xbiquge.la/40/40411/21313294.html');
INSERT INTO `book_list` VALUES ('2016', '都市小说', '国医大师', '作    者：九鼎八簋', '    一个神奇的养殖空间；\n    一身超凡脱俗的医术；\n    一位乡镇卫生院的医生，医人、医国、医民！\n    以无上信仰之力练就不灭金身！\n    看王明如何凭借自己的力量，让华夏一族一步步走上寰宇之巅。\n    让万界成为脚下的基石！\n', 'http://www.xbiquge.la/3/3320/', 'http://www.xbiquge.la/files/article/image/3/3320/3320s.jpg', '最后更新：2017-12-04 20:02:13', '一三零零', 'http://www.xbiquge.la/3/3320/8575471.html');
INSERT INTO `book_list` VALUES ('2017', '都市小说', '重生之国际倒爷', '作    者：吹牛小王呀', '    层出不穷的暴富商品。    囤积与炒作之道。    各国边境线鲸吞海吃的狼性商人。    穿越回到1992年。    重走茶马古道，西臧挖虫草，广西采珍珠，云南贩xx，腾冲倒翡翠。    开办进出口贸易公司，打造实业帝国，借东欧剧变之机，远渡俄罗斯，罗马尼亚，乌克兰，捷克斯洛伐克。    缅甸，印度，泰国。    成就一代大亨。', 'http://www.xbiquge.la/40/40065/', 'http://www.xbiquge.la/files/article/image/40/40065/40065s.jpg', '最后更新：2019-12-21 23:51:15', '第三百六十二章：是演习吗', 'http://www.xbiquge.la/40/40065/21319109.html');
INSERT INTO `book_list` VALUES ('2018', '都市小说', '穿越财富人生', '作    者：飘零幻影', '    重生78年香港创造财富帝国，打造豪门家族。同样的香江，不一样的故事。    qq群772199411', 'http://www.xbiquge.la/44/44789/', 'http://www.xbiquge.la/files/article/image/44/44789/44789s.jpg', '最后更新：2019-12-22 12:01:05', '第七百八十三章 物流布局', 'http://www.xbiquge.la/44/44789/21324278.html');
INSERT INTO `book_list` VALUES ('2019', '都市小说', '重生之创业人生', '作    者：独木桥', '    　　2003年初。　　此时，淘宝尚未成立，小米不见踪影，房价没有高不可攀，所有行业欣欣向荣……　　带着未来的记忆，陈平回到了2003，一个遍地黄金的2003。    　　', 'http://www.xbiquge.la/33/33310/', 'http://www.xbiquge.la/files/article/image/33/33310/33310s.jpg', '最后更新：2019-12-21 18:38:00', '第六百四十七章 铁血的规定', 'http://www.xbiquge.la/33/33310/21314589.html');
INSERT INTO `book_list` VALUES ('2020', '都市小说', '山沟里的制造帝国', '作    者：未语浅笑', '    1981年，祖国的改革开放事业刚刚起步，整个社会处于巨大的变革之中。    位于大山深处的牛家村，一个名叫牛小强的傻子在中暑之后变成了天才，他承包转产的工厂，在大山深处构建了一个制造业帝国，带领乡亲们走上了发家致富的光明大道。    “国企要求咱无偿贡献发动机技术？告诉他们，天黑以后再做梦。”    “外国人要制裁咱们？那咱们就互相制裁吧，到时候别哭着求我。”    “苹果公司不跟咱们合作了？那咱们就注册一家新的计算机公司吧，名字就叫苹果树！”    “国际通信协会说我们没有执行他们的标准？告诉他们，这次的标准由我来定！”    牛小强的座右铭是：“在哪里跌倒就在哪里躺着，把别人绊倒后再拍拍屁股站起来。”    这是一个天才不断抗争奋斗的故事。', 'http://www.xbiquge.la/28/28901/', 'http://www.xbiquge.la/files/article/image/28/28901/28901s.jpg', '最后更新：2019-12-22 04:01:00', '第1686章 完美的试飞表现', 'http://www.xbiquge.la/28/28901/21321736.html');
INSERT INTO `book_list` VALUES ('2021', '都市小说', '来自地狱的男人', '作    者：秋风123', '    星空魔帝，重生归来！敌人，要跪着！美女，要躺着！    “我是魔，以神为奴，以仙为仆！不服？那就杀到你服！”——夜风！', 'http://www.xbiquge.la/19/19634/', 'http://www.xbiquge.la/files/article/image/19/19634/19634s.jpg', '最后更新：2019-12-22 15:00:00', '第2168章 臣子与兄弟！', 'http://www.xbiquge.la/19/19634/21325933.html');
INSERT INTO `book_list` VALUES ('2022', '都市小说', '极品朋友圈', '作    者：水冷酒家', '    　　拳打恶霸，脚踢奸商，富甲天下，美女如云！    　　穷途末路的麦小吉，得到了一部神奇的手机，从此，他的人生就开了挂，进入孤独求败的至高境界。    于是，江湖上有个传说，遇到困难，只要高喊    “我哥们是麦小吉”，一切可解。　　可谁又知道，表面风光无限的麦小吉，暗地里却被手机朋友圈上一群恶毒的古人要挟，痛苦不堪，发红包发到心在流血。    　　麦小吉：破手机，老子要砸了你。    　　手机：丫的你试试，看谁先死！', 'http://www.xbiquge.la/23/23935/', 'http://www.xbiquge.la/files/article/image/23/23935/23935s.jpg', '最后更新：2019-12-22 14:53:42', '第1805章 都去旅游了', 'http://www.xbiquge.la/23/23935/21325962.html');
INSERT INTO `book_list` VALUES ('2023', '都市小说', '重生之校园特种兵', '作    者：大盗零零七', '    十年后的穿越重生，被称为兵种之王的特种兵王者回到了学生时代。冷艳的美女老师，可爱温柔的漂亮学姐，风、情妩、媚的邻居阿姨……逐一的上演暧、昧。 一个男孩，几个女人的荒唐生活，华丽上演。\n', 'http://www.xbiquge.la/0/50/', 'http://www.xbiquge.la/files/article/image/0/50/50s.jpg', '最后更新：2019-04-09 21:40:34', '第7954章 生日礼物', 'http://www.xbiquge.la/0/50/16797102.html');
INSERT INTO `book_list` VALUES ('2024', '都市小说', '都市之修仙归来', '作    者：布知火舞', '    【2017最火新书推荐】萧雨琪：“三年之后，你娶我可好？”楚云：“洪门千金，宅男女神，全球最美面孔排行榜三年榜首。豆蔻年华，冰清玉女。雨琪，你觉得我，配得上你吗？”萧雨琪：“我不管，我只问你一句，三年之后，你可愿娶我？”————前一世，只因楚云一句话，伊人含恨而终，楚家家破人亡。    八百年后，仙尊楚云重活地球。    “我本微末凡尘，但也心向天空。”    “这一世，我楚云只求追随本心，随性而为！”再世为人，定不负今生不负卿！    ！！', 'http://www.xbiquge.la/38/38882/', 'http://www.xbiquge.la/files/article/image/38/38882/38882s.jpg', '最后更新：2019-12-21 23:16:37', '2752.楚云的处境', 'http://www.xbiquge.la/38/38882/21318473.html');
INSERT INTO `book_list` VALUES ('2025', '都市小说', '我的尤物总裁老婆', '作    者：必须火', '    【火爆畅销】他是战场狼王，身怀绝世武功，还会神奇医术，本想平静生活，然而却奉师命下山，不得不和冰山总裁订下有名无实的婚约……', 'http://www.xbiquge.la/21/21920/', 'http://www.xbiquge.la/files/article/image/21/21920/21920s.jpg', '最后更新：2019-08-30 18:06:22', '这两天全文修改', 'http://www.xbiquge.la/21/21920/19417186.html');
INSERT INTO `book_list` VALUES ('2026', '都市小说', '嫁嫡', '作    者：木嬴', '    重生一世，她不再是那个温和纯善，安分愚孝的四姑娘。\n    阴谋诡计？争权斗宠？样样信手拈来！\n    守护好至亲，冷眼看继母嫡妹作死。\n    至于那个让她见了就绕道走的男子。\n    她已经绕道了，怎么还碰到了！\n    你问我为什么绕道？\n    我会告诉你这是上辈子绕着绕着养成的不良恶习？\n    谦让是美德！\n', 'http://www.xbiquge.la/3/3168/', 'http://www.xbiquge.la/files/article/image/3/3168/3168s.jpg', '最后更新：2016-09-16 21:13:16', '第六百八十四章 大结局', 'http://www.xbiquge.la/3/3168/5639299.html');
INSERT INTO `book_list` VALUES ('2027', '都市小说', '三国之鬼神无双', '作    者：坐井观天的青蛙', '    看惯了吕奉先、赵子龙、关云长独武山河，谁说主角就不能有盖世武勇，威凌天下！？马纵横，伏波后人，自幼武痴，在一次意外中，穿越到东汉末年，竟然成为了扶风之虎马腾的长子—马羲，更且意外地发现在历史上并无记载过的这号人物，拥有着无与伦比的体魄，九牛二虎之力。且看…\n', 'http://www.xbiquge.la/2/2988/', 'http://www.xbiquge.la/files/article/image/2/2988/2988s.jpg', '最后更新：2019-06-29 15:05:01', '第二卷 马踏乱世 2630外夷犯境，虽远必诛（30）', 'http://www.xbiquge.la/2/2988/18277241.html');
