INSERT INTO `book_list` VALUES ('2060', '都市小说', '我活了上万年', '作    者：小号也疯狂', '    我活了上万年，每天重复着相同的事情，实在是无聊透顶。我想帮自己解脱，可是喝毒药把毒药消化了，上吊把绳子磨断了，抹脖子刀刃崩口了，就是死不了，你说气人不。    如果谁能弄死我，我感谢你祖宗十八辈。', 'http://www.xbiquge.la/36/36112/', 'http://www.xbiquge.la/files/article/image/36/36112/36112s.jpg', '最后更新：2019-10-09 19:49:43', '第四百八十三章 地窟大决战', 'http://www.xbiquge.la/36/36112/20044810.html');
INSERT INTO `book_list` VALUES ('2061', '都市小说', '都市神级超人', '作    者：江渚', '    普通的高三学生周文，偶然得到了一款神级套装。神装在手，天下我有！    从此学渣变学霸，凡人变超人！纵横都市，横扫强敌，为了心中的正义，书写属于他的超人传奇……', 'http://www.xbiquge.la/27/27118/', 'http://www.xbiquge.la/files/article/image/27/27118/27118s.jpg', '最后更新：2019-12-22 09:00:01', '第892章 分一杯羹', 'http://www.xbiquge.la/27/27118/21322972.html');
INSERT INTO `book_list` VALUES ('2062', '都市小说', '透视医圣', '作    者：大小写', '    林奇得到上古传承，觉醒神瞳后，拥有了不可思议的能力，他不在是一个平凡小医生，还有了很多烦恼，因为昨天冰山女院长要他约在小树林，讨论一些羞羞的事情，但是他今天已经约了热辣老师，清纯校花，高挑女警，傲娇大明星……一起学姿势。', 'http://www.xbiquge.la/24/24705/', 'http://www.xbiquge.la/files/article/image/24/24705/24705s.jpg', '最后更新：2019-12-22 00:35:11', '第2147章 一体双魂', 'http://www.xbiquge.la/24/24705/21320462.html');
INSERT INTO `book_list` VALUES ('2063', '都市小说', '香港1968', '作    者：汪公子在年', '    1968的香港是个波澜壮阔的大时代，刚应聘上香港大学哲学教师的霍耀文走上了一条不同的人生道路，有欢喜，有悲痛，自然也有豪情壮志。', 'http://www.xbiquge.la/38/38566/', 'http://www.xbiquge.la/files/article/image/38/38566/38566s.jpg', '最后更新：2019-12-21 21:09:49', '412【当远古霸主出现在人类社会】', 'http://www.xbiquge.la/38/38566/21316640.html');
INSERT INTO `book_list` VALUES ('2064', '都市小说', '草莽年代', '作    者：北海一岛', '    一头扎进历史的乱流中，回到那个黑白色彩的八十年代。此时伊人年幼，红颜未老，改革开放的春风吹遍大地，岁月正好。', 'http://www.xbiquge.la/32/32616/', 'http://www.xbiquge.la/files/article/image/32/32616/32616s.jpg', '最后更新：2019-09-06 15:58:22', '第一千零七十八章 奶爸', 'http://www.xbiquge.la/32/32616/19545368.html');
INSERT INTO `book_list` VALUES ('2065', '都市小说', '超级小医生', '作    者：真庸', '    本是混吃等死的富家子弟，却家族蒙难，父母失踪，落魄街头。同学欺辱，兄弟反叛，订好的婚约无故作废！    一朝觉醒，奋勇向前，我虽良善，但绝不懦弱，我虽医者仁心，但绝不放狼归山！    新书，有空群里聊天：581439905。或者加主角微信号    “zhetengsong”（折腾宋的拼音）', 'http://www.xbiquge.la/21/21145/', 'http://www.xbiquge.la/files/article/image/21/21145/21145s.jpg', '最后更新：2019-12-21 23:53:09', '第2652章 完全压制', 'http://www.xbiquge.la/21/21145/21319467.html');
INSERT INTO `book_list` VALUES ('2066', '都市小说', '女校小保安', '作    者：素手添香', '    特种兵回归都市，应聘成了女生宿舍的保安。战场杀敌我在行，可是对付这么多莺莺燕燕，该怎么办才好？    保护美女校花，寻找战友妹妹，重回都市，强者终会崛起！', 'http://www.xbiquge.la/21/21559/', 'http://www.xbiquge.la/files/article/image/21/21559/21559s.jpg', '最后更新：2019-01-05 21:00:00', '第5429章 大结局', 'http://www.xbiquge.la/21/21559/14308503.html');
INSERT INTO `book_list` VALUES ('2067', '都市小说', '娱乐韩娱', '作    者：左岸浅行', '    娱乐圈不就是用来娱乐的吗？\n    林安然回头看了看残酷的现实生活，最终决定还是向前走，因为前面有一大波美女正在靠近，有年上的是熟女，年下的是萝莉，还有……厄，年中的是什么？\n', 'http://www.xbiquge.la/6/6220/', 'http://www.xbiquge.la/files/article/image/6/6220/6220s.jpg', '最后更新：2019-07-17 19:05:00', '第三四三五章 RM虚拟游戏世界——决战前夕', 'http://www.xbiquge.la/6/6220/18642892.html');
INSERT INTO `book_list` VALUES ('2068', '都市小说', '超级战兵', '作    者：一丝不苟', '    末世“神级”异能强者，重生都市，灵魂附在了特种兵叶天辰的身上。\n    被别人退婚的苦逼？NO！\n    被家族抛弃，给父母带来耻辱？NO！\n    啥叫风流？让校花痴情难忘，让女明星欲罢不能，让身家千亿的倾世美女朝思暮想；啥叫狂？拳打高富帅，脚踩官二代，只认理，不认人！\n    废物？\n    我要所有人知道，我叶天辰不是废物，我是超级战兵！\n', 'http://www.xbiquge.la/0/58/', 'http://www.xbiquge.la/files/article/image/0/58/58s.jpg', '最后更新：2019-07-20 10:55:00', '第3351章-第3352章：这便是花果山？', 'http://www.xbiquge.la/0/58/18690541.html');
INSERT INTO `book_list` VALUES ('2069', '都市小说', '欧神', '作    者：多情的海参', '    灵气复苏了，无数人开始为了觉醒而努力。孤儿洪小福也觉醒了，可是他觉醒的能力好像跟别人不大一样，因为他觉醒的居然是运气。    洪小福：“为毛我的觉醒加点是加在了这种奇奇怪怪的能力上啊？”这是一个小人物一步一步终于成为全人类的吉祥物的故事……', 'http://www.xbiquge.la/34/34516/', 'http://www.xbiquge.la/files/article/image/34/34516/34516s.jpg', '最后更新：2019-12-19 22:33:20', '第五七二章 有钱人张超越', 'http://www.xbiquge.la/34/34516/21292272.html');
INSERT INTO `book_list` VALUES ('2070', '都市小说', '我的巨星女友', '作    者：缘乐', '    沐尘想平淡的生活。只是......有一个巨星女友，媒体大众似乎不允许他这般啊！    最关键的是未来岳父递过一张千万元的支票......群：867.191.257', 'http://www.xbiquge.la/40/40149/', 'http://www.xbiquge.la/files/article/image/40/40149/40149s.jpg', '最后更新：2019-12-22 12:00:00', '第五百六十二章 这都能引发功夫热 ', 'http://www.xbiquge.la/40/40149/21324252.html');
INSERT INTO `book_list` VALUES ('2071', '都市小说', '豪门赘婿', '作    者：林剑剑', '    陈扬为了钱成为上门女婿，百般受辱。直到有一天，他爸爸开着劳斯莱斯来接他回家……', 'http://www.xbiquge.la/31/31126/', 'http://www.xbiquge.la/files/article/image/31/31126/31126s.jpg', '最后更新：2019-07-18 15:24:15', '第300章 问世间情为何物，直教人生死相许！', 'http://www.xbiquge.la/31/31126/18659044.html');
INSERT INTO `book_list` VALUES ('2072', '都市小说', '最强赘婿', '作    者：彦小焱', '    　　你以为他窝囊？不，他只是不知道怎样适应这个社会！    　　你以为他普通？不，一个连神秘军署都能惊动的人，又怎么会是普通人！    　　安瑶一直以为自己的丈夫窝囊、无能、没用，等到某一天才发现，他竟然是……', 'http://www.xbiquge.la/33/33494/', 'http://www.xbiquge.la/files/article/image/33/33494/33494s.jpg', '最后更新：2019-12-22 10:00:02', '679：真人CS', 'http://www.xbiquge.la/33/33494/21323427.html');
INSERT INTO `book_list` VALUES ('2073', '都市小说', '重活一九九五', '作    者：一杆147', '    重生回到1995年，他的龙腾95才是视窗操作系统之母。\n    中国人称李飞腾是网吧之父，世界称他是互联网第一掘金者。\n    21世纪的20年代后，地球人类不得不屈服承认活着的李飞腾青年已经是一位可以影响整个世纪的伟人，一位风华正茂的网络大神。\n    ......\n', 'http://www.xbiquge.la/2/2878/', 'http://www.xbiquge.la/files/article/image/2/2878/2878s.jpg', '最后更新：2017-11-09 06:45:59', '第1187 八抬大轿', 'http://www.xbiquge.la/2/2878/7213229.html');
INSERT INTO `book_list` VALUES ('2074', '都市小说', '官涯无悔', '作    者：关越今朝', '    因爱生变故，弃教入仕途，身世离奇出，纷杂情感路。\n    他揣着受伤的心，步入仕途，昔日园丁，转眼变身公仆。\n    宦海风起云涌，如何搏浪击流？情路纷杂，何方是归处？离奇身世，徒增几许变数。\n    坎坷仕途前行数载，当初所谓的尊严已经找到，只是此时已没有了期望中的快感。心中留存的却是大众无数，但他无悔曾经的冬夏春秋。\n    且看主人公如何官场、情路竞风流。\n', 'http://www.xbiquge.la/9/9593/', 'http://www.xbiquge.la/files/article/image/9/9593/9593s.jpg', '最后更新：2019-01-06 09:00:00', '第两千一百九十章 为民无悔（终结篇）', 'http://www.xbiquge.la/9/9593/14321106.html');
INSERT INTO `book_list` VALUES ('2075', '都市小说', '重生之游戏大亨', '作    者：成刚', '    重生回到九八年，那些消逝在时光中的遗憾，通通可以弥补了。', 'http://www.xbiquge.la/24/24568/', 'http://www.xbiquge.la/files/article/image/24/24568/24568s.jpg', '最后更新：2019-12-22 12:32:01', '第八百四十八章  源能量', 'http://www.xbiquge.la/24/24568/21324608.html');
INSERT INTO `book_list` VALUES ('2076', '都市小说', '妖孽学霸', '作    者：维斯特帕列', '    简介：学霸分为三种，第一种高级学霸，他们是各自领域的顶尖存在，比如袁隆平、高琨；第二种传奇学霸，他们的影响力远远超过了自身涉及的领域，对整个时代产生影响，比如费米、图灵；第三种神级学霸，他们影响着整个人类文明的发展，比如爱因斯坦、牛顿。在此之外还有一种妖孽，他们不仅在科研领域远超群伦，在其他领域同样做到了凡人做不到的事情，柴恩博士在45年获得诺贝尔奖，七年后在赫尔辛基奥运会上摘取金牌；尼尔斯－波尔，二十世纪最伟大的物理学家之一，同时还是丹麦国家队的守门员；当然还有薛定谔，除了那只著名的猫，还有无数让人艳羡不已的风流韵事。而吕丘建正是要成为这第四种学霸。\n', 'http://www.xbiquge.la/6/6986/', 'http://www.xbiquge.la/files/article/image/6/6986/6986s.jpg', '最后更新：2018-12-25 17:52:46', '新书《诸天万界神龙系统》已发', 'http://www.xbiquge.la/6/6986/16754637.html');
INSERT INTO `book_list` VALUES ('2077', '都市小说', '满级导演', '作    者：孜然腰花', '    在系统里苦学十八年终于出山的满级年轻导演，一头闯入了五光十色的浮华世界。    导演，明星，平行世界，系统，华娱。无脑残套路。', 'http://www.xbiquge.la/35/35414/', 'http://www.xbiquge.la/files/article/image/35/35414/35414s.jpg', '最后更新：2019-12-22 01:50:09', '第五百一十五章 很棒', 'http://www.xbiquge.la/35/35414/21321153.html');
INSERT INTO `book_list` VALUES ('2078', '都市小说', '天启之门', '作    者：跳舞', '    当过着自己正常而平凡的生活，求学，恋爱，工作，努力买房子，还房贷，单身狗也好，屌丝也罢……\n    你有没有察觉过，这个世界其实哪里不对？\n    当忽然有一天，你看见有一群奇怪的人，降临在这个世界。\n    他们，奇装异服；他们，能力超凡；他们，冷酷嗜杀！\n    他们，自称为……。\n    而你，在他们眼里，只是一堆……经验值？\n    你会选择……反抗吗？\n    ……\n    “我一定要，活下去！！！”——陈小练。\n    ……\n', 'http://www.xbiquge.la/1/1433/', 'http://www.xbiquge.la/files/article/image/1/1433/1433s.jpg', '最后更新：2019-04-03 19:05:00', '第七百五十三章 【围杀荷鲁斯】', 'http://www.xbiquge.la/1/1433/16647840.html');
INSERT INTO `book_list` VALUES ('2079', '都市小说', '武道天下', '作    者：邪影', '    顾璀璨历史，多少辉煌朝代？多少风流人物？秦皇汉武，无数天骄！\n    现代青年成为武则天的堂哥，闯荡在武道盛行，神奇玄妙的新世界！\n    武将铁血铸军魂，文谋风云炼丹心。\n    铸铁血军魂，睥睨隋唐，纵横秦汉魏吴，镇压各族文明，领略各个辉煌朝代，誓与天骄试比高！\n    ……\n    铁杆群：277291131，会员群：64442046\n    交流微信：xieyingbenji（邪影本纪的拼音）\n    已完本《铸圣庭》、《邪影本纪》、《圣儒》等长篇，值得入坑，新书更需辛勤呵护，收藏、推荐，谢谢！\n', 'http://www.xbiquge.la/7/7115/', 'http://www.xbiquge.la/files/article/image/7/7115/7115s.jpg', '最后更新：2019-12-14 11:42:44', '第一千九百九十三章 无敌天龙', 'http://www.xbiquge.la/7/7115/21202753.html');
INSERT INTO `book_list` VALUES ('2080', '都市小说', '修罗天尊', '作    者：始于梦', '    一个神秘的少年，携吞天神兽，以身成魔，杀遍九天万界，屠戮万千神魔，成就至高无上的修罗天尊。\n    ————\n    修罗天尊书友群：6092583（聊天打屁所用！）\n    修罗天尊家园群：54993257（讨论剧情所用，喜欢闲聊的勿扰！）\n    始于梦微信公共订阅号：shiyum888\n', 'http://www.xbiquge.la/3/3167/', 'http://www.xbiquge.la/files/article/image/3/3167/3167s.jpg', '最后更新：2016-06-03 12:10:25', '第2318章 无天和倪业业的怒火', 'http://www.xbiquge.la/3/3167/4475311.html');
INSERT INTO `book_list` VALUES ('2081', '都市小说', '他出自地府', '作    者：李家浮图', '    莫言不报应，神鬼有安排。地府，排名第一的佣兵组织，凶威显赫，震慑全球。    牛头马面勾魂，黑白无常索命。这个故事，从一个名为阎帝的男人，自地府走出开始……', 'http://www.xbiquge.la/31/31273/', 'http://www.xbiquge.la/files/article/image/31/31273/31273s.jpg', '最后更新：2019-12-22 01:06:08', '第1728章 叔叔', 'http://www.xbiquge.la/31/31273/21320673.html');
INSERT INTO `book_list` VALUES ('2082', '都市小说', '妖孽弃少在都市', '作    者：君梦无痕', '    他是神殿昊天大帝，统御无尽星河，却不料，被属下背叛，联合其他神殿强者，偷袭于他。    一代至尊，就此身陨，却不料，重生到了一个地球弃少身上。江湖有路，弃少称尊！    且看他，只手舞风云！喜欢本书吗？喜欢哪一个角色？', 'http://www.xbiquge.la/36/36460/', 'http://www.xbiquge.la/files/article/image/36/36460/36460s.jpg', '最后更新：2019-11-02 11:59:43', '第六百二十四章 十方神主《大结局》', 'http://www.xbiquge.la/36/36460/20392417.html');
INSERT INTO `book_list` VALUES ('2083', '都市小说', '田园牧场', '作    者：若忘书', '    都市白领刘云轩得到神奇空间，毅然返回田园。\n    以微薄的资本，以小博大，只身闯美帝。\n    买几个牧场，养几群牛羊，再养几只萌宠。\n    改良牧场，研发肥料。想喝红酒，自己酿。\n    田园牧场的生活就是这么简单又悠闲。\n    （本书无修真、无高科技，有的只是平凡）\n', 'http://www.xbiquge.la/2/2473/', 'http://www.xbiquge.la/files/article/image/2/2473/2473s.jpg', '最后更新：2016-09-18 05:56:46', '完本感言', 'http://www.xbiquge.la/2/2473/5732697.html');
INSERT INTO `book_list` VALUES ('2084', '都市小说', '绝品仙医', '作    者：白色的夜晚', '    一个绝世的炼丹天才，一个可以穿梭时空的流光梭……\n    当林天风睁开眼睛的时候，发现周围的一切都变了，所有的往事都已经是过往云烟，而他却成为了林家弃少。\n    群花萦绕，倾国倾城，当林天风站在这个世界巅峰的时候，却惊骇的发现……\n    一朝风云起，天地我为王，哥不是废物，哥是绝世天才！\n    （老白有几本百万字的完本作品，品质保证，请书友们放心！）\n', 'http://www.xbiquge.la/2/2770/', 'http://www.xbiquge.la/files/article/image/2/2770/2770s.jpg', '最后更新：2016-09-17 02:17:02', '江山红颜_1590混沌之神（完结）', 'http://www.xbiquge.la/2/2770/5652892.html');
INSERT INTO `book_list` VALUES ('2085', '都市小说', '修仙归来之都市至尊', '作    者：写到手抽筋', '    一代修仙天骄陨落在雷劫之下，重回千年之前，花花都市，我为至尊。    前世遭受的羞辱，今生我将百倍奉还；前世辜负的爱人，此生我来守护！', 'http://www.xbiquge.la/21/21272/', 'http://www.xbiquge.la/files/article/image/21/21272/21272s.jpg', '最后更新：2019-12-22 00:20:00', '第六百二十八章 落荒而逃', 'http://www.xbiquge.la/21/21272/21320375.html');
