INSERT INTO `book_list` VALUES ('2088', '都市小说', '神级美食主播', '作    者：黑色花灯', '    平行空间，华夏国。    A：第一，坐等叶飞大神开播。    B：今天叶大神要直播什么菜？猜对有奖。    C：这个真不好猜，他会做的太多了。    D：妈妈，叶飞哥哥快开播了，快给我泡一碗泡面。    E：小飞要开播了啊？老头子，别打牌了，回家看这小子玩直播去了。    F：小高，一会儿叶飞直播的时候我没办法看，有个几十亿的项目要和客户谈，这样，你帮我把他这期的节目录下来，送走客户之后我要第一眼就看到他的直播。    一个乞丐站在世纪广场上，抬头眯着眼睛盯着一块巨大的液晶显示屏，然后从兜里掏出半个黑乎乎的馒头，咬了一口，满足道：“叶飞的美食直播真的太神奇了，看他的节目，就算是一坨翔也能吃出山珍海味的味道来。”', 'http://www.xbiquge.la/15/15736/', 'http://www.xbiquge.la/files/article/image/15/15736/15736s.jpg', '最后更新：2019-12-21 22:15:27', '第一千八百四十九章 凤从西来，龙出东方', 'http://www.xbiquge.la/15/15736/21317463.html');
INSERT INTO `book_list` VALUES ('2089', '都市小说', '我的极品护士老婆', '作    者：穿高跟的猫', '    【专治美女不服】未婚妻竟是个羞羞哒的极品护士？天天上演极致诱惑？唐夜修行归来混都市，医武兼具，收得了妹子，救得了苍生，打得了畜生！\n    ...\n', 'http://www.xbiquge.la/14/14567/', 'http://www.xbiquge.la/files/article/image/14/14567/14567s.jpg', '最后更新：2019-07-22 14:20:00', '第2802章 不可控制！', 'http://www.xbiquge.la/14/14567/18719798.html');
INSERT INTO `book_list` VALUES ('2090', '都市小说', '全职赘婿', '作    者：房东老才', '    和女神上司结婚，入赘豪门，软饭从天而降，才二十出头就过上了混吃等死的生活。    你以为这很轻松？吃软饭可是一门技术活！', 'http://www.xbiquge.la/31/31670/', 'http://www.xbiquge.la/files/article/image/31/31670/31670s.jpg', '最后更新：2019-12-21 18:00:00', '番外：快递（二）', 'http://www.xbiquge.la/31/31670/21314194.html');
INSERT INTO `book_list` VALUES ('2091', '都市小说', '权力巅峰', '作    者：梦入洪荒', '    军人出身的柳擎宇，毅然转业进入官场，成为乡镇镇长，然而上任当天却被完全架空，甚至被晾在办公室无人理睬！\n    且看脾气火爆，办事雷厉风行的他，如何凭借着机智头脑和层出不穷的手段，翻手间覆灭种种阴谋，步步高升！\n    几十次微妙的官位升迁，数千场激烈的明争暗斗争，历经波折，踏上权力的巅峰！\n', 'http://www.xbiquge.la/0/41/', 'http://www.xbiquge.la/files/article/image/0/41/41s.jpg', '最后更新：2017-12-16 00:09:53', '第四篇 受屈辱愤怒反击', 'http://www.xbiquge.la/0/41/8758463.html');
INSERT INTO `book_list` VALUES ('2092', '都市小说', '至尊归来', '作    者：白虹贯月', '    一代仙尊重生归来，两年前的秦朗含冤入狱，两年后出狱却看到陷害自己的人嚣张的来接他出狱，而秦朗曾经追过的女孩却靠在那人的身上……', 'http://www.xbiquge.la/22/22986/', 'http://www.xbiquge.la/files/article/image/22/22986/22986s.jpg', '最后更新：2019-07-26 13:18:38', '第3381章  结局', 'http://www.xbiquge.la/22/22986/18774350.html');
INSERT INTO `book_list` VALUES ('2093', '都市小说', '我的完美总裁老婆', '作    者：减肥茶', '    他是战场上的死亡收割机，暗黑世界最为神秘的‘八爷’！    却因为一场婚约，龙潜花都！    “老婆，你竟然让我去找小姐？”叶凡脑袋摇的像是拨浪鼓一样：    “不行！不行！你老公不是那种人！我……没钱！”    “什么？你给钱？好吧，看在你这么诚心的份上，我就放过你闺蜜了！”    清冷未婚妻、性感警花，妖娆老板娘……一个个美女纷至沓来，叶凡很紧张：    “喂喂！美女，有话好好说，你……你别脱……”    作者自定义标签:                                                淡定                                                无敌文                                                赚钱', 'http://www.xbiquge.la/25/25971/', 'http://www.xbiquge.la/files/article/image/25/25971/25971s.jpg', '最后更新：2019-07-17 08:00:00', '第2620章惹怒我', 'http://www.xbiquge.la/25/25971/18634048.html');
INSERT INTO `book_list` VALUES ('2094', '都市小说', '我的空间门', '作    者：烦恼的香烟', '    我的空间门最新章节,我的空间门无弹窗,我的空间门全文阅读.\n', 'http://www.xbiquge.la/3/3577/', 'http://www.xbiquge.la/files/article/image/3/3577/3577s.jpg', '最后更新：2016-03-12 23:53:23', '第三〇六章 得道仙真（大结局）', 'http://www.xbiquge.la/3/3577/3736923.html');
INSERT INTO `book_list` VALUES ('2095', '都市小说', '重任', '作    者：曲封', '    【起点第一编辑组签约作品】\n    ......\n    起点首部铁路题材作品\n    ......\n    看一个小小的列车员是如何登上权力颠峰的\n    .......\n    ......\n    ......\n    VIP群：24634740（只限VIP会员加入，VIP昵称验证）\n    读者群：89169802（欢迎加入，感谢一骑绝尘提供）\n', 'http://www.xbiquge.la/0/563/', 'http://www.xbiquge.la/files/article/image/0/563/563s.jpg', '最后更新：2016-09-16 18:33:36', '第1268章  主政辽东（大结局）', 'http://www.xbiquge.la/0/563/5632531.html');
INSERT INTO `book_list` VALUES ('2096', '都市小说', '全能透视', '作    者：寻北仪_杭州悦蓝', '    苏齐获得神秘宙眼,观古今,测未来,鉴古董,赌玉石,成为最年轻的鉴宝大师!美女别动,你胸前玉坠是商纣王送给妲己的定情信物,可否让某把玩一番?\n', 'http://www.xbiquge.la/15/15252/', 'http://www.xbiquge.la/files/article/image/15/15252/15252s.jpg', '最后更新：2019-02-10 17:19:43', '默认卷_第一千四百一十九章：未来之门（大结局）', 'http://www.xbiquge.la/15/15252/15211652.html');
INSERT INTO `book_list` VALUES ('2097', '都市小说', '土豪美利坚', '作    者：五陵狗熊', '    没有空间，没有古董；但是有着一块神奇的玉牌，有着神奇的传承。    做牧场主、成海岛主......这些都不是最重要的。    魏达就是要享受人生，享受那神奇的一切。    我有一所房子，面朝大海，春暖花开！    我有一片牧场，纵马逍遥快意一生！    ......\n', 'http://www.xbiquge.la/1/1952/', 'http://www.xbiquge.la/files/article/image/1/1952/1952s.jpg', '最后更新：2015-05-17 17:18:53', '第717章  终章', 'http://www.xbiquge.la/1/1952/1439567.html');
INSERT INTO `book_list` VALUES ('2098', '都市小说', '大国高科', '作    者：房产大亨', '    我们的骨子里，就流淌着创新进取的血。要是连我们都不来搞高科创新！    那么未来世界的话语权在哪里？而在九十年代的某一天，张东升很轻蔑的告诉外国投资者：“我不但不给你便宜，还要抬高价格，因为全世界除了我这儿你无处可买……”', 'http://www.xbiquge.la/35/35845/', 'http://www.xbiquge.la/files/article/image/35/35845/35845s.jpg', '最后更新：2019-12-02 19:35:00', '第558章 逃婚（大结局）', 'http://www.xbiquge.la/35/35845/20967345.html');
INSERT INTO `book_list` VALUES ('2099', '都市小说', '上门女婿', '作    者：貌似纯洁', '    韩东本身是一个    “被”退伍的军人。回到都市后，被迫成为了夏家的上门女婿……', 'http://www.xbiquge.la/26/26508/', 'http://www.xbiquge.la/files/article/image/26/26508/26508s.jpg', '最后更新：2019-07-23 10:25:39', '第一卷 入赘豪门_第一千二百零六章 浮沉', 'http://www.xbiquge.la/26/26508/18732062.html');
INSERT INTO `book_list` VALUES ('2100', '都市小说', '老婆的头号黑粉', '作    者：暮夏二十', '    穿越平行世界，拥有了一个简单又平凡的家庭，江北只想和谐幸福的度过这一生。    但是他发现，自己的老婆竟然是一个小明星。    明星？不喜欢老婆当明星。    ……    若干年以后。    记者：江先生您好，请问您太太的事业有如此巨大的成就，这里面是否有您的功劳呢？    江北：有吧。    记者：可是据我们的调查发现，在幼幼出道的这些年里，您曾注册过1958个微博账号、3240个贴吧账号、以及各大论坛账号黑过她，有人怀疑您就是多年以来一直潜伏在黑暗中的黑粉头子，不知您对此有什么解释呢？    江北：谣言，都是谣言。', 'http://www.xbiquge.la/43/43474/', 'http://www.xbiquge.la/files/article/image/43/43474/43474s.jpg', '最后更新：2019-12-20 23:53:16', '第二百一十九章 两老儿公园初相逢 （2/2）', 'http://www.xbiquge.la/43/43474/21306706.html');
