INSERT INTO `book_list` VALUES ('2113', '都市小说', '学霸的黑科技时代', '作    者：咬文嚼纸', '    高三弱鸡男萧铭，在一次熬夜看小说猝死后经历了高等智慧星球潘沙星科技战士的全部人生。    死而复生的萧铭发现理科课程变得简单起来，此外他还拥有了潘沙星的科技系统。    在高考的关口，他开启了不一样的人生。    从污水处理技术到核心芯片；    ……    从DNA维修商店到意识上传服务器；    一项项改变生活的黑科技应运而生。    登上《时代》封面，问鼎《福布斯》头版。    《福布斯》：“这个男人垄断了全球，我们已经无法核算他的身价……”    科技创造未来，萧铭要做未来的引路者。    萧铭：“不好意思，我好像带歪了人类的科技树。”    （PS：继续幻想科技）    已经完本：《第四生物帝国》，正在解禁中《位面黑科技》，坑品保证。', 'http://www.xbiquge.la/34/34802/', 'http://www.xbiquge.la/files/article/image/34/34802/34802s.jpg', '最后更新：2019-12-21 22:47:49', '第816章 浪漫旅行吗？星际那种', 'http://www.xbiquge.la/34/34802/21317927.html');
INSERT INTO `book_list` VALUES ('2114', '都市小说', '重生文娱洪流', '作    者：戒酒的剑仙', '    周不然带着地球的文化宝藏穿越到平行世界，发誓要成为大明星！他被誉为娱乐圈的一股毒流，但他却自诩是清流！    不安分的他却今天和大作家撕逼，明天又脚踩人气小鲜肉，就这样开始他传奇的明星之路！', 'http://www.xbiquge.la/33/33121/', 'http://www.xbiquge.la/files/article/image/33/33121/33121s.jpg', '最后更新：2019-12-21 21:39:22', '707 觉不觉得他像个人？', 'http://www.xbiquge.la/33/33121/21316960.html');
INSERT INTO `book_list` VALUES ('2115', '都市小说', '重生日本高校生', '作    者：李童', '    李学浩在族谱的封皮中找到一本修道秘笈，花了十年时间终于筑基成功，不料急于求成之下走火入魔，身死之后重生到了日本的横滨市，附身于一个刚升入高中的高一学生身上……    PS：公布个Q群，333638358，欢迎大家！\n', 'http://www.xbiquge.la/10/10529/', 'http://www.xbiquge.la/files/article/image/10/10529/10529s.jpg', '最后更新：2019-09-29 23:30:00', '第2110章 花心的表姐夫', 'http://www.xbiquge.la/10/10529/19912189.html');
INSERT INTO `book_list` VALUES ('2116', '都市小说', '一号警官', '作    者：一号狙击手', '    公安大全优学员丁凡，阴差阳错的分配到东北兴安岭深处的偏远派出所，不仅要在老警们的严管下奋起，甚至被犯罪分子当面侮辱！    本领无限，浑身是胆的他，凭借机敏反应和取之不竭的手段，翻手为云覆手为雨，挫败一个个阴谋，摧毁一伙伙罪犯，步步艰难，屡屡升迁！    波涛汹涌，智高凭栏，实实在在保护国家利益和百姓安危！警之初，如利剑，从警一生，重在民安！    群号：562076523', 'http://www.xbiquge.la/28/28125/', 'http://www.xbiquge.la/files/article/image/28/28125/28125s.jpg', '最后更新：2019-12-21 20:02:01', '第1229章 杀人计划', 'http://www.xbiquge.la/28/28125/21315627.html');
INSERT INTO `book_list` VALUES ('2117', '都市小说', '透视小医神', '作    者：小萌狼', '    妩媚总裁想和他睡，火辣女警花要他做男朋友，长腿白富美逼他当未婚夫，还有个可爱邻家女孩在长期性发花痴。\n    身怀绝技的狡猾乡村小神医，顿时头疼了起来，不过还好，我是个博爱的人，让别人没妞可泡的事情我特喜欢。\n    身怀绝技踏出小山村，赚最多的钱，泡最美的妞，吊打各种不服，成就一段牛叉人生。\n', 'http://www.xbiquge.la/13/13945/', 'http://www.xbiquge.la/files/article/image/13/13945/13945s.jpg', '最后更新：2019-01-10 08:08:42', '第118章 吻', 'http://www.xbiquge.la/13/13945/14420319.html');
INSERT INTO `book_list` VALUES ('2118', '都市小说', '重生之大娱乐家系统', '作    者：潇潇清枫', '    上一世的三流歌手叶文轩，带着大娱乐家系统重生异世！    诸多世界娱乐霸主面对着来自东方的淩霸，咬牙切齿。    “斗破系列都已经拍了快七部了，还没拍完？！”    “吞噬系列居然拍了十三部还没拍完！什么？还有将近十部才完结？”    “谁能告诉我这个叫做叶文轩的人，到底是什么时候崛起的！”    遥远在东方的叶文轩，站在一座世纪宏伟的建筑之上，打了一个喷嚏。    “哎，拥有一个世界的资源做后援，赢得简直太简单了！”    回头望着身后的两个美若天仙的妻子，叶文轩觉得，这就是人生的巅峰！    （书友群：六三五三六三三四二）', 'http://www.xbiquge.la/16/16245/', 'http://www.xbiquge.la/files/article/image/16/16245/16245s.jpg', '最后更新：2018-10-26 14:00:00', '新书《文娱不朽》已发', 'http://www.xbiquge.la/16/16245/11979436.html');
INSERT INTO `book_list` VALUES ('2119', '都市小说', '我有未来科技系统', '作    者：姜汁糖水', '    高三学生叶宇，获得未来超级智脑他的人生轨迹发生巨大变化。他靠着非人的头脑和努力，一步步走上巅峰。    他是天才少年、科技狂魔、商界传奇、新时代的谱写者……【科技爽文】', 'http://www.xbiquge.la/42/42104/', 'http://www.xbiquge.la/files/article/image/42/42104/42104s.jpg', '最后更新：2019-12-22 12:39:12', '第475章奖励沈美君和黛娜', 'http://www.xbiquge.la/42/42104/21324664.html');
INSERT INTO `book_list` VALUES ('2120', '都市小说', '我真不想躺赢啊', '作    者：太白猫', '    当徐茫打算努力的时候，老天爷找到了他，结果徐茫放了一个很臭的屁，老天爷被熏走了...    从此徐茫的人生只有躺赢...    语文课睡觉，解锁语文技能！    数学课睡觉，解锁数学技能！”    早上赖会床，解锁赖床技能！    总之当徐茫打算躺下的时候，全世界都会给他让路。    ————    这是一个学渣绑定‘躺赢’系统后，慢慢躺向人生巅峰的，轻松欢乐爆笑的故事。', 'http://www.xbiquge.la/37/37439/', 'http://www.xbiquge.la/files/article/image/37/37439/37439s.jpg', '最后更新：2019-12-22 09:40:54', '第七百四十六章 目标世界第一（1/3）', 'http://www.xbiquge.la/37/37439/21323292.html');
INSERT INTO `book_list` VALUES ('2121', '都市小说', '韩娱之掌控星光', '作    者：飘雨的飞絮', '    苏瑜浩，一个普普通通的大专毕业生，在失恋迷茫之际，偶得系统的帮助，从而踏上了追求梦想的道路。\n    当苏瑜浩遇到爱情，有了一点成绩之后，开始心安理得的享受生活，品味爱情，却疏忽了系统。\n    花样百出的系统却利用自己的神秘开始一次又一次的鞭策苏瑜浩在梦想的路上努力拼搏，去完成心底最真，最美的梦想。\n    本书讲述的就是一个性格随遇而安，被系统逼迫从而奋发图强的故事，这里面，有欢笑，有痛苦，有成功的喜悦，还有心底无奈的吐槽。\n', 'http://www.xbiquge.la/2/2416/', 'http://www.xbiquge.la/files/article/image/2/2416/2416s.jpg', '最后更新：2019-07-12 17:53:00', '3332 婚后众女的安排', 'http://www.xbiquge.la/2/2416/18551343.html');
INSERT INTO `book_list` VALUES ('2122', '都市小说', '重生之妖孽人生', '作    者：金老虎', '    重生后的李豪发现还有三天就要高考了……校花原来是对他有意的超级大明星竟然成了他的病人公安局长成了他的小弟一不小心还收购了路虎公司这一次，他决定彻底改写废柴的一生，成为最闪耀的天王巨星！    嗯，还以为我会去混娱乐圈，但那些一哥一姐都是我公司旗下艺人。好吧，我是妖孽，你咬我啊！', 'http://www.xbiquge.la/21/21672/', 'http://www.xbiquge.la/files/article/image/21/21672/21672s.jpg', '最后更新：2019-07-16 15:44:06', '第2941章 交代后事', 'http://www.xbiquge.la/21/21672/18616175.html');
INSERT INTO `book_list` VALUES ('2123', '都市小说', '都市超级全职系统', '作    者：羊的浮生梦', '    他，因体内封印凶兽混沌，被命运抛弃，成为一颗漂泊的种子来到了平凡的星球。    偶然获得了他爷爷创造的系统，从此命运的齿轮开始转动！！他，是雇佣兵界的王，亚洲第一杀手！    地下皇帝！名侦探！大明星！全球最大的企业老板！他独爱一人，可上天却要捉弄他们两人。    她嫣然笑着说：万水千山就当作伏笔，我总会等到姗姗来迟你。他霸道笑着说：我站在所有的巅峰，只为告诉全世界的人，你是我的女人！', 'http://www.xbiquge.la/27/27583/', 'http://www.xbiquge.la/files/article/image/27/27583/27583s.jpg', '最后更新：2019-09-13 19:41:16', '第683章 中秋特别篇PE1', 'http://www.xbiquge.la/27/27583/20081789.html');
INSERT INTO `book_list` VALUES ('2124', '都市小说', '重生之奶爸医圣', '作    者：超爽黑啤', '    仙帝重生，成为一代医圣，还直接当了奶爸，孩儿她妈美若天仙，乖女儿非吵着要找二妈三妈，这孩子实在太懂事了，哈哈哈……', 'http://www.xbiquge.la/21/21203/', 'http://www.xbiquge.la/files/article/image/21/21203/21203s.jpg', '最后更新：2019-08-13 09:06:09', '第一千零二十五章 王者归来', 'http://www.xbiquge.la/21/21203/19043056.html');
INSERT INTO `book_list` VALUES ('2125', '都市小说', '至尊兵王', '作    者：卓公子', '    【原名《风流特种兵》】\n    特种兵王龙归故里，艳遇不断：清纯少女、成熟美妇一个不少，更有异国风情上门；铲内奸、除外患，且看叶枫如何一路香艳旖旎、成就至尊美誉\n', 'http://www.xbiquge.la/3/3410/', 'http://www.xbiquge.la/files/article/image/3/3410/3410s.jpg', '最后更新：2016-09-08 17:32:20', '第一千四百五十八章 万里雪飘', 'http://www.xbiquge.la/3/3410/5012116.html');
INSERT INTO `book_list` VALUES ('2126', '都市小说', 'sorry！有钱真能为所欲为', '作    者：花肆爷', '    钱是万能的吗？有钱能够买到一切吗？自从张狂收到财神爷送的一个亿后，他发现……sorry，有钱真能为所欲为！    我是财神爷，现在点击收藏追书，我一人打一百万，在线等！', 'http://www.xbiquge.la/28/28846/', 'http://www.xbiquge.la/files/article/image/28/28846/28846s.jpg', '最后更新：2019-05-28 16:24:56', '第492章 大结局（六千字大章）', 'http://www.xbiquge.la/28/28846/17635364.html');
INSERT INTO `book_list` VALUES ('2127', '都市小说', '妙手神农', '作    者：夜猛', '    古老的东方，生活着龙的传人，千万年来，龙的传人坚信龙的存在，我们一起期待，获得龙珠的余飞，如何从小农民起步，阅尽天下美女，拳打装逼犯，脚蹬二世祖，玩转世界各国，让我泱泱中华腾飞而起，屹立于世界之巅，壮哉我中华之龙！', 'http://www.xbiquge.la/28/28169/', 'http://www.xbiquge.la/files/article/image/28/28169/28169s.jpg', '最后更新：2019-12-22 12:26:00', '第一千六百一十四章    寄养', 'http://www.xbiquge.la/28/28169/21324570.html');
INSERT INTO `book_list` VALUES ('2128', '都市小说', '绝世武魂', '作    者：疯魔萧', '    前世即将登临顶峰之时，被所爱之人与最好的兄弟背叛，九龙武魂被夺，携绝焱神心逃离，封云修转世重生，前世欠我的，今生都要还回来！\n', 'http://www.xbiquge.la/15/15234/', 'http://www.xbiquge.la/files/article/image/15/15234/15234s.jpg', '最后更新：2019-06-21 23:35:00', '第3578章 依靠', 'http://www.xbiquge.la/15/15234/18083788.html');
INSERT INTO `book_list` VALUES ('2129', '都市小说', '我不是超级警察', '作    者：我唐', '    “我不是超级警察，我只是警界传说！”警校毕业生肖然有个小目标，成为世界上最好的警察，于是就这样实现了……', 'http://www.xbiquge.la/26/26831/', 'http://www.xbiquge.la/files/article/image/26/26831/26831s.jpg', '最后更新：2019-11-25 19:05:17', '完本感言', 'http://www.xbiquge.la/26/26831/20828441.html');
INSERT INTO `book_list` VALUES ('2130', '都市小说', '极品修真邪少', '作    者：花好月圆', '    【五百万字巅峰都市】步入红尘世俗中的叶凡，就像那高速公路上的拖拉机，想不拉风都难。    管你是垃圾还是巨人，统统踩在脚下！英雄、狗熊，神仙、神棍，在叶大爷的面前，一概下跪！    千里遁移，世界由我做主，一对天眼，览尽天下群芳。', 'http://www.xbiquge.la/20/20725/', 'http://www.xbiquge.la/files/article/image/20/20725/20725s.jpg', '最后更新：2019-12-18 14:43:30', '新书发布及一些其他信息', 'http://www.xbiquge.la/20/20725/21273998.html');
