INSERT INTO `book_list` VALUES ('2164', '都市小说', '神奈川的高校生道士', '作    者：李童', '    李学浩在族谱的封皮中找到一本修道秘笈，花了十年时间终于筑基成功，不料急于求成之下走火入魔，身死之后重生到了日本的横滨市，附身于鹤见区的樱野高等学校一个高一学生真中浩二的身上，故事从这里展开……\n', 'http://www.xbiquge.la/8/8251/', 'http://www.xbiquge.la/files/article/image/8/8251/8251s.jpg', '最后更新：2019-09-29 23:30:00', '第2110章 花心的表姐夫', 'http://www.xbiquge.la/8/8251/19912188.html');
