INSERT INTO `book_list` VALUES ('2915', '网游小说', '战争天堂', '作    者：静谧长夜', '    各位狂热的虚拟游戏玩家们：    对千篇一律的线性剧情感到厌倦了吗？想要尝试真正的开放世界游戏吗？    玩腻了只能扮演一名角色的RPG？想要在虚拟现实中率领部下，体验统御千军的快感吗？    欢迎来到大规模开放式杀戮游戏“战争天堂”。    ……    看到这款游戏的胜负判定机制，某个专注PVP二十年，而且还对AI非常感兴趣的职业玩家，露出灿烂的笑容：    “是时候表演真正的杀人手段了……顺便组建一支AI军队如何？”', 'http://www.xbiquge.la/16/16020/', 'http://www.xbiquge.la/files/article/image/16/16020/16020s.jpg', '最后更新：2019-12-19 00:36:29', '1361 荆棘分歧之路', 'http://www.xbiquge.la/16/16020/21282427.html');
INSERT INTO `book_list` VALUES ('2916', '网游小说', '湾区之王', '作    者：磨砚少年', '    “中国人，在橄榄球的圈子里绝对不可能取得成功！”    “你柔弱得就好像小鹿斑比，根本无法形成对抗！”    “你有头脑、有智慧，但缺乏天赋。”陆恪：但，我有系统。手持系统，我要站上巅峰舞台，建立属于我的王朝！    书友群号：680508338', 'http://www.xbiquge.la/15/15430/', 'http://www.xbiquge.la/files/article/image/15/15430/15430s.jpg', '最后更新：2019-11-08 15:25:28', '新书发布：绿湾奇迹', 'http://www.xbiquge.la/15/15430/20558842.html');
INSERT INTO `book_list` VALUES ('2917', '网游小说', '从小李飞刀开始', '作    者：春风渭水', '    江湖里有什么？有悲天悯人例不虚发小李飞刀、有五百年来出手最干净利落的大盗萧十一郎、有会灵犀一指四条眉毛的陆小凤。    当然，还少不了美人与烈酒。夏云墨在老家找到了一本古书，当翻开古书的时候，他来到了小说中的世界。    一切，从小李飞刀开始。', 'http://www.xbiquge.la/22/22297/', 'http://www.xbiquge.la/files/article/image/22/22297/22297s.jpg', '最后更新：2019-10-20 01:22:12', '咳咳，', 'http://www.xbiquge.la/22/22297/20165689.html');
INSERT INTO `book_list` VALUES ('2918', '网游小说', '武侠重生', '作    者：大帅匪', '    武侠重生最新章节,武侠重生无弹窗,武侠重生全文阅读.\n', 'http://www.xbiquge.la/3/3597/', 'http://www.xbiquge.la/files/article/image/3/3597/3597s.jpg', '最后更新：2017-11-09 06:48:14', '完本感言！', 'http://www.xbiquge.la/3/3597/7280477.html');
